import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useLocation,
} from "react-router-dom";
import { createPortal } from "react-dom";
import "./styles.css";

// Firebase imports
// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyBeZ5Zt0KVaee67rWMFqMoOLbTV7Rvk1Zo",
  authDomain: "restaurant-ea8e8.firebaseapp.com",
  projectId: "restaurant-ea8e8",
  storageBucket: "restaurant-ea8e8.firebasestorage.app",
  messagingSenderId: "615424724015",
  appId: "1:615424724015:web:ba16efcbb59bf8ed1f4ada",
  measurementId: "G-ZZSK6XW14H"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);

// Отримання меню з Firestore
const fetchMenuItems = async () => {
  const menuCol = collection(db, "menuItems");
  const menuSnapshot = await getDocs(menuCol);
  const menuList = menuSnapshot.docs.map((doc) => doc.data());
  return menuList;
};

const specialOffers = [
  {
    name: "2-for-1 Margherita Pizza",
    price: "$10",
    image:
      "https://uk.ooni.com/cdn/shop/articles/20220211142645-margherita-9920_e41233d5-dcec-461c-b07e-03245f031dfe.jpg?v=1737105431&width=1080",
  },
  {
    name: "10% Off Large Order",
    price: "$16",
    image:
      "https://www.mymoderncookery.com/wp-content/uploads/2017/12/Simple-Italian-Chopped-Salad-10-of-11.jpg",
  },
];

const Modal = ({ children, onClose }) => {
  return createPortal(
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {children}
        <center>
          <Button onClick={onClose} text="Close" />
        </center>
      </div>
    </div>,
    document.getElementById("modal-root")
  );
};

const Button = ({ onClick, text }) => (
  <button className="btn" onClick={onClick}>
    {text}
  </button>
);

const Header = () => (
  <header className="header">
    <h1>Gourmet Restaurant</h1>
    <nav>
      <Link to="/">Home</Link>
      <Link to="/cart">Cart</Link>
      <Link to="/offers">Special Offers</Link>
    </nav>
  </header>
);

const Footer = () => (
  <footer className="footer">
    <p>&copy; 2025 Gourmet Restaurant. All rights reserved.</p>
  </footer>
);

const MenuItem = ({ item, onAdd }) => (
  <div className="card">
    <img src={item.image} alt={item.name} />
    <h3>{item.name}</h3>
    <p>{item.price}</p>
    <Button onClick={() => onAdd(item)} text="Add" />
  </div>
);

const MenuList = ({ items, onAdd }) => (
  <div className="menu-list">
    {items.map((item, index) => (
      <div key={index}>
        <MenuItem item={item} onAdd={onAdd} />
      </div>
    ))}
  </div>
);

const MainPage = ({ menuItems, onAddToCart }) => (
  <div className="main-page">
    <center>
      <h2 className="fade-in">Menu</h2>
    </center>
    <MenuList items={menuItems} onAdd={onAddToCart} />
  </div>
);

const CartPage = ({ cart, onClearCart, onRemoveFromCart }) => {
  const total = cart.reduce((sum, item) => {
    const price = parseFloat(item.price.replace("$", ""));
    return sum + (isNaN(price) ? 0 : price);
  }, 0);

  return (
    <div className="cart-page">
      <center>
        <h2>Your Cart</h2>
      </center>
      <h3>Total: ${total.toFixed(2)}</h3>
      {cart.length > 0 && (
        <>
          <Button onClick={onClearCart} text="Clear Cart" />
          <Link to={`/payment?total=${total.toFixed(2)}`}>
            <Button text="Pay" />
          </Link>
        </>
      )}
      <div className="menu-list">
        {cart.map((item, index) => (
          <div key={index} style={{ width: "50%" }}>
            <div className="card">
              <img src={item.image} alt={item.name} />
              <h3>{item.name}</h3>
              <p>{item.price}</p>
              <Button onClick={() => onRemoveFromCart(index)} text="Remove" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const PaymentPage = () => {
  const location = useLocation();
  const query = new URLSearchParams(location.search);
  const total = parseFloat(query.get("total")) || 0;

  return (
    <div className="payment-page">
      <center>
        <h2>Payment</h2>
      </center>
      <form className="payment-form">
        <label>
          Name on Card:
          <input type="text" required />
        </label>
        <label>
          Card Number:
          <input type="text" required placeholder="xxxx-xxxx-xxxx-xxxx" />
        </label>
        <label>
          Expiry Date:
          <input type="month" required />
        </label>
        <label>
          CVV:
          <input type="password" maxLength="4" required />
        </label>
        <label>
          Total to Pay:
          <input type="text" value={`$${total.toFixed(2)}`} disabled />
        </label>
        <Button
          onClick={() => alert("Payment submitted!")}
          text="Submit Payment"
        />
      </form>
    </div>
  );
};

const OffersPage = ({ onAddToCart }) => (
  <div className="offers-page">
    <center>
      <h2>Special Offers & Discounts</h2>
    </center>
    <MenuList items={specialOffers} onAdd={onAddToCart} />
  </div>
);

const App = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [cart, setCart] = useState([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchMenuItems().then(setMenuItems);
  }, []);

  const handleAddToCart = (item) => {
    setCart([...cart, item]);
    setShowModal(true);
  };

  const handleRemoveFromCart = (indexToRemove) => {
    setCart(cart.filter((_, index) => index !== indexToRemove));
  };

  return (
    <Router>
      <div className="main-layout">
        <Header />
        <main className="main-content">
          <Routes>
            <Route
              path="/"
              element={
                <MainPage menuItems={menuItems} onAddToCart={handleAddToCart} />
              }
            />
            <Route
              path="/cart"
              element={
                <CartPage
                  cart={cart}
                  onClearCart={() => setCart([])}
                  onRemoveFromCart={handleRemoveFromCart}
                />
              }
            />
            <Route path="/payment" element={<PaymentPage />} />
            <Route path="/offers" element={<OffersPage onAddToCart={handleAddToCart} />} />
          </Routes>
          {showModal && (
            <Modal onClose={() => setShowModal(false)}>
              <center>
                <p>Item added to cart!</p>
              </center>
            </Modal>
          )}
        </main>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
