import React, { useState, useEffect, createContext, useContext } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useParams, useNavigate } from "react-router-dom";
import "./styles.css";

// Firebase
import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore";

const firebaseConfig = {
  apiKey: "YOUR_API_KEY",
  authDomain: "YOUR_PROJECT_ID.firebaseapp.com",
  projectId: "YOUR_PROJECT_ID",
  storageBucket: "YOUR_PROJECT_ID.appspot.com",
  messagingSenderId: "YOUR_SENDER_ID",
  appId: "YOUR_APP_ID"
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

// Cart context
const CartContext = createContext();

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (item) => {
    setCartItems((prev) => [...prev, item]);
    alert(`${item.name} added to cart!`);
  };

  const removeFromCart = (index) => {
    const updated = [...cartItems];
    updated.splice(index, 1);
    setCartItems(updated);
  };

  const clearCart = () => setCartItems([]);

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart }}>
      <Router>
        <div className="container">
          <header>
            <h1>Virtual Menu</h1>
            <nav>
              <Link to="/">Menu</Link>
              <Link to="/cart">Cart ({cartItems.length})</Link>
            </nav>
          </header>

          <Routes>
            <Route path="/" element={<CategoriesPage />} />
            <Route path="/category/:tag" element={<CategoryPage />} />
            <Route path="/cart" element={<CartPage />} />
            <Route path="/payment" element={<PaymentPage />} />
          </Routes>

          <footer>
            <p>&copy; 2025 Virtual Restaurant</p>
          </footer>
        </div>
      </Router>
    </CartContext.Provider>
  );
};

// Categories page
const CategoriesPage = () => {
  const categories = [
    { name: "Pizza", tag: "pizza", image: "https://cdn-icons-png.flaticon.com/512/3132/3132693.png" },
    { name: "Dessert", tag: "dessert", image: "https://cdn-icons-png.flaticon.com/512/1046/1046784.png" },
    { name: "Drink", tag: "drink", image: "https://cdn-icons-png.flaticon.com/512/685/685352.png" },
    { name: "Salad", tag: "salad", image: "https://cdn-icons-png.flaticon.com/512/1046/1046857.png" },
    { name: "Spaghetti", tag: "spaghetti", image: "https://cdn-icons-png.flaticon.com/512/1046/1046759.png" },
    { name: "Special Offer", tag: "special-offer", image: "https://cdn-icons-png.flaticon.com/512/888/888879.png" }
  ];

  return (
    <div className="grid">
      {categories.map((cat) => (
        <Link to={`/category/${cat.tag}`} key={cat.tag}>
          <div className="card">
            <img src={cat.image} alt={cat.name} />
            <h3>{cat.name}</h3>
          </div>
        </Link>
      ))}
    </div>
  );
};

// Category items page
const CategoryPage = () => {
  const { tag } = useParams();
  const [items, setItems] = useState([]);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    const fetchData = async () => {
      const querySnapshot = await getDocs(collection(db, "menuItems"));
      const data = [];
      querySnapshot.forEach((doc) => {
        if (doc.data().tag === `/tags/${tag}`) data.push(doc.data());
      });
      setItems(data);
    };
    fetchData();
  }, [tag]);

  return (
    <div>
      <h2 className="section-title">{tag.replace("-", " ").toUpperCase()}</h2>
      <div className="grid">
        {items.map((item, index) => (
          <div className="card" key={index}>
            <img src={item.image} alt={item.name} />
            <h3>{item.name}</h3>
            <p>${item.price}</p>
            <button onClick={() => addToCart(item)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

// Cart page
const CartPage = () => {
  const { cartItems, removeFromCart, clearCart } = useContext(CartContext);
  const total = cartItems.reduce((sum, item) => sum + item.price, 0).toFixed(2);
  const navigate = useNavigate();

  return (
    <div>
      <h2 className="section-title">Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <div className="grid">
            {cartItems.map((item, i) => (
              <div className="card" key={i}>
                <img src={item.image} alt={item.name} />
                <h3>{item.name}</h3>
                <p>${item.price}</p>
                <button onClick={() => removeFromCart(i)}>Remove</button>
              </div>
            ))}
          </div>
          <div className="total">
            <h3>Total: ${total}</h3>
            <button onClick={() => navigate("/payment")}>Proceed to Payment</button>
            <button onClick={clearCart}>Clear Cart</button>
          </div>
        </>
      )}
    </div>
  );
};

// Payment page
const PaymentPage = () => {
  const { cartItems, clearCart } = useContext(CartContext);
  const [form, setForm] = useState({ name: "", email: "", card: "" });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();
  const total = cartItems.reduce((sum, item) => sum + item.price, 0).toFixed(2);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const validate = () => {
    let errs = {};
    if (!form.name.trim()) errs.name = "Name is required.";
    if (!form.email.match(/^[^\s@]+@[^\s@]+\.[^\s@]+$/)) errs.email = "Valid email required.";
    if (!form.card.match(/^\d{16}$/)) errs.card = "16-digit card number required.";
    setErrors(errs);
    return Object.keys(errs).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      alert(`Payment of $${total} successful!`);
      clearCart();
      navigate("/");
    }
  };

  return (
    <div>
      <h2 className="section-title">Payment</h2>
      <form className="payment-form" onSubmit={handleSubmit}>
        <label>
          Name:
          <input name="name" value={form.name} onChange={handleChange} />
          {errors.name && <span className="error">{errors.name}</span>}
        </label>
        <label>
          Email:
          <input name="email" value={form.email} onChange={handleChange} />
          {errors.email && <span className="error">{errors.email}</span>}
        </label>
        <label>
          Card Number:
          <input name="card" value={form.card} onChange={handleChange} maxLength="16" />
          {errors.card && <span className="error">{errors.card}</span>}
        </label>
        <label>
          Total:
          <input value={`$${total}`} readOnly />
        </label>
        <button type="submit">Pay</button>
      </form>
    </div>
  );
};

export default App;
