import React, { useEffect, useState } from "react";
import { db } from "./firebaseConfig";
import { collection, getDocs } from "firebase/firestore";
import "./styles.css";

function App() {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMenuItems = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "menuItems"));
        const items = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setMenuItems(items);
        setLoading(false);
      } catch (error) {
        console.error("❌ Error fetching menu items:", error);
      }
    };

    fetchMenuItems();
  }, []);

  if (loading) return <p className="fade-in">Loading menu...</p>;

  return (
    <div className="main-layout">
      <header className="header">
        <h1>Virtual Menu</h1>
        <nav>
          <a href="#pizza">Pizza</a>
          <a href="#dessert">Dessert</a>
          <a href="#drink">Drinks</a>
          <a href="#salad">Salads</a>
          <a href="#spaghetti">Spaghetti</a>
          <a href="#special">Special Offers</a>
        </nav>
      </header>

      <main className="main-content">
        {["pizza", "dessert", "drink", "salad", "spaghetti", "special-offer"].map(category => (
          <section key={category} id={category} className="fade-in">
            <h2 style={{ textAlign: "center" }}>{category.replace("-", " ").toUpperCase()}</h2>
            <div className="menu-list">
              {menuItems
                .filter(item => item.tag.includes(category))
                .map(item => (
                  <div key={item.id} className="card">
                    <img src={item.image} alt={item.name} />
                    <h3>{item.name}</h3>
                    <p>Price: ${item.price}</p>
                    <button className="btn">Add to cart</button>
                  </div>
                ))}
            </div>
          </section>
        ))}
      </main>

      <footer className="footer">
        <p>© 2025 Virtual Menu</p>
      </footer>
    </div>
  );
}

export default App;
