import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from "react-router-dom";
import { createPortal } from "react-dom";
import "./styles.css";

// Mock backend call
const fetchMenuItems = () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve([
        {
          name: "Margherita Pizza",
          price: "$10",
          image: "https://uk.ooni.com/cdn/shop/articles/20220211142645-margherita-9920_e41233d5-dcec-461c-b07e-03245f031dfe.jpg?v=1737105431&width=1080",
        },
        {
          name: "Caesar Salad",
          price: "$7",
          image: "https://images.immediate.co.uk/production/volatile/sites/30/2020/08/recipe-image-legacy-id-327831_11-3524329.jpg?quality=90&resize=440,400",
        },
        {
          name: "Spaghetti Bolognese",
          price: "$12",
          image: "https://www.valdemarsro.dk/wp-content/2015/09/bolognese-1.jpg",
        },
      ]);
    }, 1000);
  });
};

const specialOffers = [
  {
    name: "2-for-1 Margherita Pizza",
    price: "$10",
    image: "https://uk.ooni.com/cdn/shop/articles/20220211142645-margherita-9920_e41233d5-dcec-461c-b07e-03245f031dfe.jpg?v=1737105431&width=1080",
  },
  {
    name: "10% Off Large Order",
    price: "$0",
    image: "https://upload.wikimedia.org/wikipedia/commons/2/2f/Discount_tag_icon.png",
  },
];

const Modal = ({ children, onClose }) => {
  return createPortal(
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {children}
        <Button onClick={onClose} text="Close" />
      </div>
    </div>,
    document.getElementById("modal-root")
  );
};

const Button = ({ onClick, text }) => (
  <button className="btn" onClick={onClick}>{text}</button>
);

const Header = () => (
  <header className="header">
    <h1>Gourmet Restaurant</h1>
    <nav>
      <Link to="/">Home</Link>
      <Link to="/cart">Cart</Link>
      <Link to="/offers">Special Offers</Link>
    </nav>
  </header>
);

const Footer = () => (
  <footer className="footer">
    <p>&copy; 2025 Gourmet Restaurant. All rights reserved.</p>
  </footer>
);

const MenuItem = ({ item, onAdd }) => (
  <div className="card">
    <img src={item.image} alt={item.name} />
    <h3>{item.name}</h3>
    <p>{item.price}</p>
    <Button onClick={() => onAdd(item)} text="Add" />
  </div>
);

const MenuList = ({ items, onAdd }) => (
  <div className="menu-list">
    {items.map((item, index) => (
      <div key={index}>
        <MenuItem item={item} onAdd={onAdd} />
      </div>
    ))}
  </div>
);

const MainPage = ({ menuItems, onAddToCart }) => (
  <div className="main-page">
    <h2 className="fade-in">Menu</h2>
    <MenuList items={menuItems} onAdd={onAddToCart} />
  </div>
);

const CartPage = ({ cart, onClearCart, onRemoveFromCart }) => {
  const total = cart.reduce((sum, item) => {
    const price = parseFloat(item.price.replace("$", ""));
    return sum + (isNaN(price) ? 0 : price);
  }, 0);

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>
      <h3>Total: ${total.toFixed(2)}</h3>
      {cart.length > 0 && (
        <>
          <Button onClick={onClearCart} text="Clear Cart" />
          <Link to={`/payment?total=${total.toFixed(2)}`}>
            <Button text="Pay" />
          </Link>
        </>
      )}
      <div className="menu-list">
        {cart.map((item, index) => (
          <div key={index} style={{ width: "75%" }}>
            <div className="card">
              <img src={item.image} alt={item.name} />
              <h3>{item.name}</h3>
              <p>{item.price}</p>
              <Button onClick={() => onRemoveFromCart(index)} text="Remove" />
            </div>
          </div>
        ))}
      </div>

    </div>
  );
};

const PaymentPage = () => {
  const location = useLocation();
  const query = new URLSearchParams(location.search);
  const total = parseFloat(query.get("total")) || 0;

  return (
    <div className="payment-page">
      <h2>Payment</h2>
      <form className="payment-form">
        <label>
          Name on Card:
          <input type="text" required />
        </label>
        <label>
          Card Number:
          <input type="text" required placeholder="xxxx-xxxx-xxxx-xxxx" />
        </label>
        <label>
          Expiry Date:
          <input type="month" required />
        </label>
        <label>
          CVV:
          <input type="password" maxLength="4" required />
        </label>
        <label>
          Total to Pay:
          <input type="text" value={`$${total.toFixed(2)}`} disabled />
        </label>
        <Button onClick={() => alert("Payment submitted!")} text="Submit Payment" />
      </form>
    </div>
  );
};

const OffersPage = ({ onAddToCart }) => (
  <div className="offers-page">
    <h2>Special Offers & Discounts</h2>
    <MenuList items={specialOffers} onAdd={onAddToCart} />
  </div>
);

const App = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [cart, setCart] = useState([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchMenuItems().then(setMenuItems);
  }, []);

  const handleAddToCart = (item) => {
    setCart([...cart, item]);
    setShowModal(true);
  };

  const handleRemoveFromCart = (indexToRemove) => {
    setCart(cart.filter((_, index) => index !== indexToRemove));
  };

  return (
    <Router>
      <div className="main-layout">
        <Header />
        <main className="main-content">
          <Routes>
            <Route path="/" element={<MainPage menuItems={menuItems} onAddToCart={handleAddToCart} />} />
            <Route path="/cart" element={
              <CartPage
                cart={cart}
                onClearCart={() => setCart([])}
                onRemoveFromCart={handleRemoveFromCart}
              />
            } />
            <Route path="/payment" element={<PaymentPage />} />
            <Route path="/offers" element={<OffersPage onAddToCart={handleAddToCart} />} />
          </Routes>
          {showModal && (
            <Modal onClose={() => setShowModal(false)}>
              <center>
              <p>Item added to cart!</p>
              </center>
            </Modal>
          )}
        </main>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
