import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import { createPortal } from "react-dom";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import "./styles.css";

// Mock backend call
const fetchMenuItems = () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve([{
    name: "Margherita Pizza",
    price: "$10",
    image: "https://uk.ooni.com/cdn/shop/articles/20220211142645-margherita-9920_e41233d5-dcec-461c-b07e-03245f031dfe.jpg?v=1737105431&width=1080",
  },
  {
    name: "Caesar Salad",
    price: "$7",
    image: "https://example.com/caesar.jpg",
  },
  {
    name: "Spaghetti Bolognese",
    price: "$12",
    image: "https://example.com/spaghetti.jpg",
  },
      ]);
    }, 1000);
  });
};

const Modal = ({ children, onClose }) => {
  return createPortal(
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {children}
        <Button onClick={onClose} text="Close" />
      </div>
    </div>,
    document.getElementById("modal-root")
  );
};

const Button = ({ onClick, text }) => (
  <button className="btn" onClick={onClick}>{text}</button>
);

const Header = () => (
  <header className="header">
    <h1>Gourmet Restaurant</h1>
    <nav>
      <Link to="/">Home</Link>
      <Link to="/cart">Cart</Link>
      <Link to="/payment">Payment</Link>
      <Link to="/offers">Special Offers</Link>
    </nav>
  </header>
);

const Footer = () => (
  <footer className="footer">
    <p>&copy; 2025 Gourmet Restaurant. All rights reserved.</p>
  </footer>
);

const MenuItem = ({ item, onAdd }) => (
  <div className="card">
    <img src={item.image} alt={item.name} />
    <h3>{item.name}</h3>
    <p>{item.price}</p>
    <Button onClick={() => onAdd(item)} text="Add" />
  </div>
);


const MenuList = ({ items, onAdd }) => (
  <ul className="menu-list">
    {items.map((item) => (
      <MenuItem key={item.id} item={item} onAdd={onAdd} />
    ))}
  </ul>
);

const AddItemForm = ({ onAdd }) => {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd({ id: Date.now(), name, price });
    setName("");
    setPrice("");
  };

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Dish name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Price"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
        required
      />
      <Button type="submit" text="Add Dish" />
    </form>
  );
};

const MainPage = ({ menuItems, onAddToCart, onAddNewDish }) => (
  <div className="main-page">
    <h2 className="fade-in">Menu</h2>
    <AddItemForm onAdd={onAddNewDish} />
    <MenuList items={menuItems} onAdd={onAddToCart} />
  </div>
);

const CartPage = ({ cart, onClearCart }) => (
  <div className="cart-page">
    <h2>Your Cart</h2>
    <ul>
      {cart.map((item, index) => (
        <li key={index}>{item.name} - {item.price}</li>
      ))}
    </ul>
    {cart.length > 0 && (
      <Button onClick={onClearCart} text="Clear Cart" />
    )}
  </div>
);


const PaymentPage = () => (
  <div className="payment-page">
    <h2>Payment</h2>
    <p>Mock payment form goes here...</p>
  </div>
);

const OffersPage = () => (
  <div className="offers-page">
    <h2>Special Offers & Discounts</h2>
    <ul>
      <li>Buy one get one free on Margherita Pizza</li>
      <li>10% off for orders over $50</li>
    </ul>
  </div>
);

const App = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [cart, setCart] = useState([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchMenuItems().then(setMenuItems);
  }, []);

  const handleAddToCart = (item) => {
    setCart([...cart, item]);
    setShowModal(true);
  };

  const handleAddNewDish = (item) => {
    setMenuItems([...menuItems, item]);
  };

  return (
    <Router>
      <div className="main-layout">
      <Header />
      <main className="main-content">
        <Routes>
          <Route path="/" element={<MainPage menuItems={menuItems} onAddToCart={handleAddToCart} onAddNewDish={handleAddNewDish} />} />
          <Route path="/cart" element={<CartPage cart={cart} onClearCart={() => setCart([])} />}/>          
          <Route path="/payment" element={<PaymentPage />} />
          <Route path="/offers" element={<OffersPage />} />
        </Routes>
        {showModal && (
          <Modal onClose={() => setShowModal(false)}>
            <p>Item added to cart!</p>
          </Modal>
        )}
      </main>
      <Footer />
    </div>
    </Router>
  );
};

export default App;
