// App.js
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useParams } from "react-router-dom";
import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs, query, where, addDoc, deleteDoc, doc } from "firebase/firestore";
import "./styles.css";

const firebaseConfig = {
  apiKey: "YOUR_API_KEY",
  authDomain: "YOUR_PROJECT_ID.firebaseapp.com",
  projectId: "YOUR_PROJECT_ID",
  storageBucket: "YOUR_PROJECT_ID.appspot.com",
  messagingSenderId: "YOUR_SENDER_ID",
  appId: "YOUR_APP_ID"
};


const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

const categories = [
  { name: "Pizza", path: "pizza", image: "https://veteranopizza.com/public/uploads/site_catalog/original_15f5a6329654e1ce4ea27ecef1620d0a.jpg" },
  { name: "Dessert", path: "dessert", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVT62BEzNhxBnHnBEliCcXw2TeKhpwb93LArHcnWhn7918cYxGKhT7DRGiBZeAZ9TCcKE&usqp=CAU" },
  { name: "Drink", path: "drink", image: "https://cdnimg.webstaurantstore.com/images/products/large/473808/1928112.jpg" },
  { name: "Salad", path: "salad", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQPUtfY_88tQAY7dteBgpKC2njMfq7mxMJPvA&s" },
  { name: "Spaghetti", path: "spaghetti", image: "https://realfood.tesco.com/media/images/1400x919-SpaghettiCarbonara-557b6ff5-c4f3-4565-ae8e-a506f7dcc415-0-1400x919.jpg" },
  { name: "Special Offers", path: "special-offer", image: "https://cdn-icons-png.flaticon.com/512/899/899667.png" },
];

const Menu = () => {
  const navigate = useNavigate();
  return (
    <div className="categories-container">
      {categories.map((cat, index) => (
        <div key={index} className="category-card" onClick={() => navigate(`/category/${cat.path}`)}>
          <img src={cat.image} alt={cat.name} />
          <h3>{cat.name}</h3>
        </div>
      ))}
    </div>
  );
};

const Category = ({ addToCart }) => {
  const { tag } = useParams();
  const [items, setItems] = useState([]);
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const q = query(collection(db, "menuItems"), where("tag", "==", "/tags/" + tag));
      const querySnapshot = await getDocs(q);
      const results = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setItems(results);
    };
    fetchData();
  }, [tag]);

  const handleAdd = (item) => {
    addToCart(item);
    setShowPopup(true);
    setTimeout(() => setShowPopup(false), 2000);
  };

  return (
    <div className="menu-list">
      {items.map(item => (
        <div key={item.id} className="card">
          <img src={item.image} alt={item.name} />
          <h3>{item.name}</h3>
          <p>{item.price} $</p>
          <button className="btn" onClick={() => handleAdd(item)}>Add to Cart</button>
        </div>
      ))}
      {showPopup && <div className="modal-overlay"><div className="modal-content">Added to cart!</div></div>}
    </div>
  );
};

const Cart = ({ cart, removeFromCart, clearCart }) => {
  const navigate = useNavigate();
  const total = cart.reduce((sum, item) => sum + item.price, 0);

  return (
    <div className="main-content">
      <h2 style={{ textAlign: "center" }}>Cart Total: ${total.toFixed(2)}</h2>
      <div className="menu-list">
        {cart.map((item, idx) => (
          <div key={idx} className="card">
            <img src={item.image} alt={item.name} />
            <h3>{item.name}</h3>
            <p>{item.price} $</p>
            <button className="btn" onClick={() => removeFromCart(idx)}>Remove</button>
          </div>
        ))}
      </div>
      {cart.length > 0 && (
        <div style={{ textAlign: "center", margin: "2rem" }}>
          <button className="btn" onClick={() => navigate("/checkout")}>Pay</button>
          <button className="btn" onClick={clearCart}>Clear</button>
        </div>
      )}
    </div>
  );
};

const Checkout = ({ cart }) => {
  const total = cart.reduce((sum, item) => sum + item.price, 0);
  const [name, setName] = useState("");
  const [card, setCard] = useState("");
  const [valid, setValid] = useState(false);

  const validate = () => {
    setValid(name.trim() !== "" && /^\d{16}$/.test(card));
  };

  return (
    <form className="payment-form" onSubmit={(e) => e.preventDefault()}>
      <label>
        Name:
        <input value={name} onChange={e => setName(e.target.value)} onBlur={validate} required />
      </label>
      <label>
        Card Number (16 digits):
        <input value={card} onChange={e => setCard(e.target.value)} onBlur={validate} required maxLength={16} />
      </label>
      <label>
        Total:
        <input value={`$${total.toFixed(2)}`} disabled />
      </label>
      <button className="btn" disabled={!valid}>Pay Now</button>
    </form>
  );
};

const App = () => {
  const [cart, setCart] = useState([]);
  const addToCart = item => setCart([...cart, item]);
  const removeFromCart = index => setCart(cart.filter((_, i) => i !== index));
  const clearCart = () => setCart([]);

  return (
    <Router>
      <header className="header">
        <nav>
          <Link to="/">Menu</Link>
          <Link to="/cart">Cart ({cart.length})</Link>
        </nav>
      </header>
      <Routes>
        <Route path="/" element={<Menu />} />
        <Route path="/category/:tag" element={<Category addToCart={addToCart} />} />
        <Route path="/cart" element={<Cart cart={cart} removeFromCart={removeFromCart} clearCart={clearCart} />} />
        <Route path="/checkout" element={<Checkout cart={cart} />} />
      </Routes>
    </Router>
  );
};

export default App;
